class GeminiService {
  static async queryGemini(query) {
    try {
      const response = await fetch(`https://api.gemini.com/query/v1?q=${encodeURIComponent(query)}`, {
        headers: {
          'Authorization': 'Bearer AIzaSyBIJOPH26-9JVs6rw8801a4IGyB65ZUtQQ',
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      return data.result;
    } catch (error) {
      console.error('Error querying Gemini:', error);
      return 'Sorry, I encountered an error while processing your request.';
    }
  }
}

export default GeminiService;
