const { GoogleGenerativeAI } = require("@google/generative-ai");
const genAI = new GoogleGenerativeAI(process.env.GOOGLE_API_KEY);

const generateContent = async (prompt, modelName) => {
  try {
    const model = genAI.getGenerativeModel({ model: "gemini-1.5-flash"});
    const chat = model.startChat({ history: [] });
    const result1 = await chat.sendMessage('In one sentence, explain how a computer works to a young child.',);
    console.log(result1.response.text());
    const result2 = await chat.sendMessage('Okay, how about a more detailed explanation to a high schooler?',);
    console.log(result2.response.text());
  } catch (error) {
    console.error('Error generating content:', error.message);
    throw new Error('Failed to generate content');
  }
};

generateContent();
